import { atom } from "recoil";

import DeathsFile from '../assets/deaths.json';

const ages = DeathsFile.map(d => d.age);

const Age = atom({
    key: 'age', // unique ID (with respect to other atoms/selectors)
    default: {min: Math.min(...ages), max: Math.max(...ages)}, // default value (aka initial value)
});

export default Age;
