import { Paper } from '@mui/material';
import React, { useEffect, useLayoutEffect, useRef, useState } from 'react';
import * as d3 from "d3";
import Streets from './assets/streets.json';
import Pumps from './assets/pumps.json';
import Deaths from './assets/deaths.json';

const D3 = () => {
    const [height, setHeight] = useState(500);
    const [width, setWidth] = useState(500);
    const paperRef = useRef<HTMLElement | null>();
    const d3Ref = useRef(d3.selectAll("#paper-plane svg g"));

    useLayoutEffect(() => {
        paperRef.current = document.getElementById("paper-plane");
        if (paperRef.current) {
            setHeight(paperRef.current.clientHeight);
            setWidth(paperRef.current.clientWidth);
        }
    },[])

    const createMap = () => {
        console.log('hi')
        const margin = {top: 10, right: 30, bottom: 30, left: 60}
        const xScale = d3.scaleLinear().domain([0, 20]).range([0, width - margin.left - margin.right]);
        const yScale = d3.scaleLinear().domain([0, 20]).range([height - margin.top - margin.bottom, 0]);
        d3Ref.current = d3.selectAll("#paper-plane svg g")
        // .call(d3.zoom().on("zoom", function (e) {
        //     console.log(e)
        //     console.log(d3.event)
        //     div.attr("transform", d3.event.transform)
        // }));

        d3Ref.current.selectAll("svg")
            .attr("width", width)
            .attr("height", height)
            .append("g");

        // streets
        const streets = d3Ref.current.selectAll('polygon')
                .data(Streets)
                .enter()
                .append("polygon")
                .style("stroke", "gray")
                .style("fill", "gray")
                .style("stroke-width", 6)
                .attr("points", d => {
                    let points = "";
                    d.forEach((_, i) => points += `${xScale(d[i].x)}, ${yScale(d[i].y)} `);
                    return points;
                });
            const tooltip = d3.select("#tooltip")
                .append("div")
                    .style("position", "absolute")
                    .style("visibility", "hidden")
                    .style("padding", "3px")
                    .style("background", "gray")
                    .text("");

            //pumps
            const pumps = d3Ref.current.selectAll('circle')
                .data(Pumps)
                .enter()
                .append('circle')
                .style("fill", "deepskyblue")
                .style("r", 8)
                .attr("cx", d => xScale(d.x))
                .attr("cy", d => yScale(d.y));

            const deaths = d3Ref.current.selectAll('text')
                .data(Deaths)
                .enter()
                .append("circle")
                .style("fill", "red")
                .style("r", 2)
                .attr("cx", d => xScale(d.x))
                .attr("cy", d => yScale(d.y))
                .style("cursor", "pointer")
                .on("mouseover", (d) => tooltip.style("visibility", "visible").text(`Age: ${d.age}`))
                .on("mousemove", (d) => {
                    return tooltip.style("top", (yScale(d.y) - 15)+"px").style("left",(xScale(d.x) + 15)+"px")
                })
    }

    // useEffect(() => {
    //     createMap()
    //     console.log(height,width);
    // },[height, width]);

    return <>
    {createMap()}
    <Paper
        id="paper-plane"
        sx={{
        p: 2,
        display: 'flex',
        flexDirection: 'column',
        height: '80vh',
    }}>
        <svg>
            <g>
            </g>
        </svg>
    </Paper>
    </>;
}

export default D3;
