import { useEffect, useState } from 'react';
import { useRecoilState } from 'recoil';
import DeathsFile from './assets/deaths.json';
import Age from './State/Age';
import Sex from './State/Sex';
import { CoordinateType } from './Streets';

type DeathType = CoordinateType&{
    age: number;
    gender: number;
}

const useDeaths = () => {
    const [ deaths, setDeaths ] = useState<Array<DeathType>>(DeathsFile);
    const [ age ] = useRecoilState(Age);
    const [ sex ] = useRecoilState(Sex);

    useEffect(() => {
        setDeaths(() => {
            let deaths: Array<DeathType> = [];
            for (let d of DeathsFile) {
                if (d.age >= age.min && d.age <= age.max) {
                    if (sex !== undefined) {
                        if (d.gender === sex) {
                            deaths.push(d);
                        }
                    } else {
                        deaths.push(d);
                    }
                }
            }
            console.log(deaths)
            return deaths
        });
    },[sex, age]);

    return deaths;
}

export default useDeaths;
