import { Paper } from '@mui/material';
import React, { useEffect, useRef, useState } from 'react';
import * as d3 from "d3";
import Streets from './assets/streets.json';
import Pumps from './assets/pumps.json';
import Deaths from './assets/deaths.json';
import { BaseType } from 'd3';

function useWindowSize() {
    // Initialize state with undefined width/height so server and client renders match
    // Learn more here: https://joshwcomeau.com/react/the-perils-of-rehydration/
    const [windowSize, setWindowSize] = useState({
      width: 0,
      height: 0,
    });
    useEffect(() => {
      // Handler to call on window resize
      function handleResize() {
        // Set window width/height to state
        setWindowSize({
          width: window.innerWidth,
          height: window.innerHeight,
        });
      }
      // Add event listener
      window.addEventListener("resize", handleResize);
      // Call handler right away so state gets updated with initial window size
      handleResize();
      // Remove event listener on cleanup
      return () => window.removeEventListener("resize", handleResize);
    }, []); // Empty array ensures that effect is only run on mount
    return windowSize;
}

const margin = {top: 10, right: 30, bottom: 30, left: 60}

const D3 = () => {
    const [height, setHeight] = useState(700);
    const [width, setWidth] = useState(800);
    const paperRef = useRef<HTMLElement | null>();
    const d3Ref = useRef(d3.selectAll("#paper-plane svg g"));
    const windowSize = useWindowSize();
    let streets: d3.Selection<d3.BaseType, unknown, d3.BaseType, unknown>;
    let pumps: d3.Selection<d3.BaseType, unknown, d3.BaseType, unknown>;
    let deaths: d3.Selection<d3.BaseType, unknown, d3.BaseType, unknown>;
    const [xScale, setXScale] = useState<any>(d3.scaleLinear().domain([0, 20]).range([0, width - margin.left - margin.right]));
    const [yScale, setYScale] = useState<any>(d3.scaleLinear().domain([0, 20]).range([height - margin.top - margin.bottom, 0]));

    useEffect(()=>createMap(),[]);

    useEffect(() => {
        paperRef.current = document.getElementById("paper-plane");
        if (paperRef.current) {
            setHeight(paperRef.current.clientHeight);
            setWidth(paperRef.current.clientWidth);
        }
    },[windowSize.width, windowSize.height])

    const createMap = () => {


        d3Ref.current = d3.selectAll("#paper-plane svg g")
        // .call(d3.zoom().on("zoom", function (e) {
        //     console.log(e)
        //     console.log(d3.event)
        //     div.attr("transform", d3.event.transform)
        // }));

        d3Ref.current
            .attr("width", width)
            .attr("height", height)
            .append("g");

        // streets
        streets = d3Ref.current.selectAll('polygon')
            .data(Streets)
            .enter()
            .append("polygon")
            .style("stroke", "gray")
            .style("fill", "gray")
            .style("stroke-width", 6)
            .attr("points", d => {
                let points = "";
                d.forEach((_, i) => points += `${xScale(d[i].x)}, ${yScale(d[i].y)} `);
                return points;
            });
        const tooltip = d3.select("#tooltip")
            .append("div")
                .style("position", "absolute")
                .style("visibility", "hidden")
                .style("padding", "3px")
                .style("background", "gray")
                .text("");

        //pumps
        pumps = d3Ref.current.selectAll('circle')
            .data(Pumps)
            .enter()
            .append('circle')
            .style("fill", "deepskyblue")
            .style("r", 8)
            .attr("cx", d => xScale(d.x))
            .attr("cy", d => yScale(d.y));

        deaths = d3Ref.current.selectAll('text')
            .data(Deaths)
            .enter()
            .append("circle")
            .style("fill", "red")
            .style("r", 2)
            .attr("cx", d => xScale(d.x))
            .attr("cy", d => yScale(d.y))
            .style("cursor", "pointer")
            .on("mouseover", (d) => tooltip.style("visibility", "visible").text(`Age: ${d.age}`))
            .on("mousemove", (d) => {
                return tooltip.style("top", (yScale(d.y) - 15)+"px").style("left",(xScale(d.x) + 15)+"px")
            })
    }

    useEffect(() => {
        // d3Ref.current = d3Ref.current.remove();
        streets.attr("points", (d:any) => {
            let points = "";
            d.forEach((_:any, i: number) => points += `${xScale(d[i].x)}, ${yScale(d[i].y)} `);
            return points;
        });
        createMap()
        console.log(height,width);
        setXScale(width);
        setYScale(height);
    },[height, width]);

    return <>
    <Paper
        id="paper-plane"
        sx={{
        p: 2,
        display: 'flex',
        flexDirection: 'column',
        height: '80vh',
    }}>
        <svg height="100%">
            <g height="100%">
            </g>
        </svg>
    </Paper>
    </>;
}

export default D3;
