import React, { useEffect } from 'react';
import * as d3 from "d3";
const Legend = () => {

    useEffect(() => {
        var svg = d3.select("#my_dataviz")

        // Handmade legend
        svg.append("circle").attr("cx",20).attr("cy",10).attr("r", 6).style("fill", "deepskyblue")
        svg.append("circle").attr("cx",100).attr("cy",10).attr("r", 3).style("fill", "red")
        svg.append("line").attr("x1",180).attr("y1",10).attr("x2", 200).attr("y2", 10).style("stroke", "lightgrey").style("stroke-width", 6);

        svg.append("text").attr("x", 35).attr("y", 12).text("Pumps").style("font-size", "15px").attr("alignment-baseline","middle")
        svg.append("text").attr("x", 110).attr("y", 12).text("Deaths").style("font-size", "15px").attr("alignment-baseline","middle")
        svg.append("text").attr("x", 210).attr("y", 12).text("Streets").style("font-size", "15px").attr("alignment-baseline","middle")
    },[]);

    return <svg style={{ border: '1px solid black', float: 'right'}} id="my_dataviz" height="22" width="280px"></svg>;
}

export default Legend;
