import React from 'react';
import { FormControl, FormControlLabel, FormLabel, InputLabel, MenuItem, Radio, RadioGroup, Select, Slider } from '@mui/material';
import { useRecoilState } from 'recoil';
import Sex from './State/Sex';
import Age from './State/Age';
import Typography from '@mui/material/Typography';
import DeathsFile from './assets/deaths.json';

const ages = DeathsFile.map(d => d.age);

const Filter = () => {
    const [ sex, setSex ] = useRecoilState(Sex);
    const [ age, setAge ] = useRecoilState(Age);

    return (
        <>
        <FormControl fullWidth>
            <FormLabel id="demo-radio-buttons-group-label">Gender</FormLabel>
            <RadioGroup
                aria-labelledby="demo-radio-buttons-group-label"
                name="Gender"
                value={sex}
                onChange={(e) => {
                    console.log(e.target.value)
                    setSex(parseInt(e.target.value) as any);
                }}
            >
                <FormControlLabel value={-1} control={<Radio />} label="All" />
                <FormControlLabel value={1} control={<Radio />} label="Female" />
                <FormControlLabel value={0} control={<Radio />} label="Male" />
            </RadioGroup>
        </FormControl>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Gender</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={sex}
                    label="Gender"
                    onChange={(e) => {
                        console.log(e.target.value)
                        setSex(e.target.value as any);
                    }}
                >
                    <MenuItem value={-1}>All</MenuItem>
                    <MenuItem value={0}>Male</MenuItem>
                    <MenuItem value={1}>Female</MenuItem>
                </Select>
            </FormControl>
            <br/>
            <FormControl fullWidth>
                <Typography id="input-slider" gutterBottom>
                    Age Range
                </Typography>
                <Slider
                    getAriaLabel={() => 'Age Range'}
                    disableSwap
                    value={[age.min, age.max]}
                    onChange={(e) => {
                        //@ts-ignore
                        setAge({min: e.target?.value[0] as number, max: e?.target?.value[1]})
                    }}
                    min={Math.min(...ages)}
                    max={Math.max(...ages)}
                    valueLabelDisplay={"auto"}
                    marks={[
                        {
                            value: Math.min(...ages),
                            label: Math.min(...ages)
                        },
                        {
                            value: Math.max(...ages),
                            label: Math.max(...ages),
                        }
                    ]}
                />
            </FormControl>
        </>
    );
}

export default Filter;
