import * as d3 from 'd3';
import React, { useEffect, useRef } from 'react';
import Deaths from './assets/deaths.json';

const deathDistGender: {[key: number]: number} = {0: 0, 1: 1};

for (const d of Deaths) {
    deathDistGender[d.gender]++;
}

const DeathDistGender = [
    {
        gender: 'Male',
        count: deathDistGender[1]
    },
    {
        gender: 'Female',
        count: deathDistGender[0]
    }
];

const DeathsByGender = () => {
    const margin = 200,
    width = 400,
    height = 300
    let svg = useRef(d3.selectAll("div"));


    let xScale = d3.scaleBand().range([0, 300]).padding(0.4),
    yScale = d3.scaleLinear().range([height, 0]);

    const draw = () => {
        const chart = d3.selectAll('#death-gender svg')
        //@ts-ignore
        const chartWidth = chart.node().getBoundingClientRect().width - 90;

        d3.selectAll('#death-gender svg').append("text")
        .attr("transform", "translate(100,0)")
        .attr("x", 100)
        .attr("y", 320)
        .attr("font-size", "12px")
        .text("Gender")

        var g =  d3.selectAll('#death-gender svg')
               .attr("transform", "translate(" + 60 + "," + 30 + ")");

        xScale.domain(DeathDistGender.map((d) => d.gender.toString() ));
        yScale.domain([0, d3.max(DeathDistGender, (d) => d.count as any )]);

        g.append("g")
         .attr("transform", "translate(0," + height + ")")
         .call(d3.axisBottom(xScale))
         .append("text")
         .attr("y", height)
         .attr("x", chartWidth - 100)
         .attr("text-anchor", "end")
         .text("Gender");

        g.append("g")
         .call(d3.axisLeft(yScale).tickFormat(function(d){
             return d as any;
         })
         .ticks(10))
         .append("text")
         .attr("transform", "rotate(-90)")
         .attr("y", 4)
         .attr("dy", "-5.1em")
         .attr("text-anchor", "end")
         .attr("stroke", "black")
        //  .text("Death Count");

        g.selectAll(".bar")
         .data(DeathDistGender)
         .enter().append("rect")
         .attr("class", "bar")
         .attr("x", function(d) { return xScale(d.gender.toString()) as any; })
         .attr("y", function(d) { return yScale(d.count); })
         .attr("width", xScale.bandwidth())
         .attr("height", function(d) { return height - yScale(d.count); }).attr('fill', 'red');
    }

    useEffect(() => {
        draw();

        d3.select(window)
            .on("resize", function() {
                draw();
            });
    },[]);

    return (
        <div id="death-gender" style={{ overflow: 'visible' }}>
            <svg width="100%" style={{ overflow: 'visible' }}>
            </svg>
        </div>
    )
}

export default DeathsByGender;
