import * as d3 from 'd3';
import moment from 'moment';
import React, { useEffect, useRef } from 'react';
import { useRecoilState } from 'recoil';
import useDeathDays from './DeathDays';
import TheDate from './State/Date';

const DeathChart = () => {
    const margin = {top: 10, right: 20, bottom: 30, left: 30},
            height = 200 - margin.top - margin.bottom;
    const deathDays = useDeathDays({});
    let svg = useRef(d3.selectAll("div"));
    const [ date, setDate ] = useRecoilState(TheDate)


    let x: any;
    let y: any;

    const graph = () => {
        let formatValue = d3.format(",");

        if (svg.current) {
            svg.current.append("path")
            .datum(deathDays)
            .attr("fill", "none")
            .attr("stroke", "red")
            .attr("stroke-width", 1.5)
            //@ts-ignore
            .attr("d", d3.line()
                .x((d: any) => x(d3.timeParse("%Y-%m-%d")(d.date)))
                .y((d: any) => y(d.deaths))
            )

        var focus = svg.current.append("g")
            .attr("class", "focus")
            .style("display", "none");

        const mousemove = (e:any) => {
            const x0 = x.invert(d3.pointer(e)[0]);
            const res = deathDays.find((element) => moment(element.date).format('Y-M-D') === moment(x0).format('Y-M-D'));
            if (res) {
                setDate(res.date)
                focus.attr("transform", "translate(" + x(d3.timeParse("%Y-%m-%d")(res.date)) + "," + y(res.deaths) + ")");
                focus.select(".tooltip-date").text(res.date);
                focus.select(".tooltip-likes").text(formatValue(res.deaths));
            }

        }

        focus.append("circle")
            .attr("r", 5);

        focus.append("rect")
            .attr("class", "tooltip")
            .attr("width", 100)
            .attr("height", 50)
            .attr("x", 10)
            .attr("y", -22)
            .attr("rx", 4)
            .attr("ry", 4)
            .style("position", "absolute")
            .style("zIndex", 100);

        focus.append("text")
            .attr("class", "tooltip-date")
            .attr("x", 18)
            .attr("y", -2);

        focus.append("text")
            .attr("x", 18)
            .attr("y", 18)
            .text("Deaths:");

        focus.append("text")
            .attr("class", "tooltip-likes")
            .attr("x", 75)
            .attr("y", 18);

        var chart = d3.selectAll('#death-chart');
        //@ts-ignore
        var width = chart.node().getBoundingClientRect().width - 90;
        svg.current.append("rect")
            .attr("class", "overlay")
            .attr("width", width)
            .attr("height", height)
            .on("mouseover", function() { focus.style("display", null); })
            .on("mouseout", function() { focus.style("display", "none"); setDate(null); })
            .on("mousemove", mousemove);
        }
    }

    const draw = () => {
        var s = d3.selectAll('#death-chart svg g');
        var chart = d3.selectAll('#death-chart');
        s = s.remove();
        //@ts-ignore
        var width = chart.node().getBoundingClientRect().width - 90;
        //@ts-ignore
        svg.current = d3.select("#death-chart svg")
            .attr("width", width)
            .attr("height", height + margin.top + margin.bottom)
            .append("g")
            .attr("transform",
                "translate(" + margin.left + "," + margin.top + ")");
        const dates = deathDays.map(d => new Date(d.date));
        const domain = d3.extent(dates);
        x = d3.scaleTime()
        //@ts-ignore
        .domain(domain)
        .range([ 0, width ]);

        const xAxis = d3.axisBottom(x);

        svg.current.append("g")
        .attr("transform", "translate(0," + height + ")")
        //@ts-ignore
        .call(xAxis.ticks(d3.timeDay.every(4)).tickFormat(d3.timeFormat('%m/%d')));

        y = d3.scaleLinear()
        //@ts-ignore
        .domain([0, d3.max(deathDays, (d) => +d.deaths + 20)])
        .range([ height, 0 ]);

        svg.current.append("g")
        .call(d3.axisLeft(y));

        graph();
    }

    useEffect(() => {
        draw();

        d3.select(window)
            .on("resize", function() {
                draw();
            });
    },[]);

    useEffect(() => {
        graph();
    },[deathDays]);

    return (
        <div id="death-chart" style={{ overflow: 'visible' }}>
            <svg width="100%" style={{ overflow: 'visible' }}></svg>
        </div>
    )
}

export default DeathChart;
