import React, { useEffect, useRef, useState } from 'react';
import * as d3 from "d3";
import useWindowSize from './useWindowSize';

const margin = {top: 10, right: 60, bottom: 10, left: 0}

type Props = {
    streets: any;
    pumps: any;
    deaths: any;
}

