                .attr("cy", (d:any) => yScale(d.y))
                .attr("class", 'death')
                .style("z-index", 1)
                .style("cursor", "pointer")
                .on("mouseover", mouseOver)
                .on("mousemove", mouseMove)
                .on("mouseleave", mouseOut) as any
        } catch (e) {
            console.log(e);
        }
    },[props.deaths])

    return <>
        <div style={{ position: 'absolute', zIndex: 2 }}>
            <div id="tooltip"></div>
        </div>
        <div
            id="paper-plane"
            style={{
                // p: 2,
                display: 'flex',
                flexDirection: 'column',
                height: width,
                position: 'relative',
                zIndex: 1,
                overflow: "hidden",
            }}
        >
            <svg height="100%" style={{cursor: 'move'}}>
                <g height="100%">
                </g>
            </svg>
        </div>
    </>;
}

export default Map;
