import React, { useEffect, useRef, useState } from 'react';
import * as d3 from "d3";
import useWindowSize from './useWindowSize';

const margin = {top: 10, right: 60, bottom: 10, left: 0}

type Props = {
    streets: any;
    pumps: any;
    deaths: any;
}

const Map = (props: Props) => {
    const paperRef = useRef<HTMLElement | null>();
    const d3Ref = useRef(d3.selectAll("#paper-plane svg g"));
    const windowSize = useWindowSize();
    const [ width, setWidth ] = useState(500);
    let streets: d3.Selection<d3.BaseType, unknown, d3.BaseType, unknown>;
    let pumps: d3.Selection<d3.BaseType, unknown, d3.BaseType, unknown>;
    let deaths: d3.Selection<d3.BaseType, unknown, d3.BaseType, unknown>;
    let xScale = d3.scaleLinear().domain([0, 20]).range([0, 1000 - margin.left - margin.right]);
    let yScale = d3.scaleLinear().domain([0, 20]).range([1000 - margin.top - margin.bottom, 0]);
    let tooltip: any = null;


    useEffect(() => {
        createMap();
        d3.selectAll("#text")
            .data([
                {
                    street: 'Margaret Street',
                    x: 4,
                    y: 17,
                    rotate: -14,
                },
                {
                    street: 'Princes Street',
                    x: 3.5,
                    y: 17,
                    rotate: -14
                }
            ])
            .append('text')
            .attr("x", (d) => xScale(d.x))
            .attr("y", (d) => yScale(d.y))
            .attr("dy", ".35em")
            .style("transform", (d) => `rotate(${d.rotate}deg)`)
            .style("font-size", 11)
            .text((d) => d.street);
    }, []);

    useEffect(() => {

        paperRef.current = document.getElementById("paper-plane");
        if (paperRef.current) {
            xScale = d3.scaleLinear().domain([0, 20]).range([0, paperRef.current.clientWidth - margin.left - margin.right]);
            yScale = d3.scaleLinear().domain([0, 20]).range([paperRef.current.clientWidth - margin.top - margin.bottom, 0]);
            d3Ref.current
                .attr("width", paperRef.current.clientWidth)
                .attr("height", paperRef.current.clientWidth)
            setWidth(paperRef.current.clientWidth);
        }
    },[windowSize.width, windowSize.height]);

    useEffect(() => {
        tooltip = d3.select("#tooltip")
            .append("div")
            .style("position", "absolute")
            .style("visibility", "hidden")
            .style("padding", "10px")
            .style("border-radius", "5px")
            .style("zIndex", 100)
            .style("background", "gray")
            .style("color", "white")
            .style("height", "70px")
            .style("width", "150px")
            .text("");
    }, []);

    const mouseOver = (_: any, l: any) => {
        const gender = l.gender ? 'Female' : 'Male';   
        return tooltip.style("visibility", "visible").html(`
            <b>Age</b>: ${l.age}
            <br/>
            <b>Gender</b>: ${gender}
        `)
    }

    const mouseMove = (d: any) => tooltip.style("top", (d.y - 90)+"px").style("left",(d.x - 15)+"px");

    const mouseOut = () => tooltip.style("visibility", "hidden");

    const createMap = () => {
        d3Ref.current = d3.selectAll("#paper-plane svg g")

        const lol = d3.selectAll("#paper-plane svg")
        //@ts-ignore
        .call(d3.zoom().scaleExtent([1, 3]).on("zoom", function (e) {
            //@ts-ignore
            lol.attr("transform", e.transform)
        }));

        // streets
        streets = d3Ref.current.selectAll('polygon')
            .data(props.streets)
            .enter()
            .append("polygon")
            .style("stroke", "lightgray")
            .style("fill", "lightgray")
            .style("stroke-width", 10)
            .style("z-index", 0)
            .attr("points", (d: any) => {
                let points = "";
                d.forEach((_: any, i: string | number) => points += `${xScale(d[i].x)}, ${yScale(d[i].y)} `);
                return points;
            }) as any;

        //pumps
        pumps = d3Ref.current.selectAll('circle')
            .data(props.pumps)
            .enter()
            .append('circle')
            .style("fill", "deepskyblue")
            .style("r", 8)
            .style("z-index", 2)
            .attr("cx", (d:any) => xScale(d.x))
            .attr("cy", (d:any) => yScale(d.y)) as any;

        deaths = d3Ref.current.selectAll('text')
            .data(props.deaths)
            .enter()
            .append("circle")
            .style("fill", "red")
            .style("r", 4)
            .attr("cx", (d:any) => xScale(d.x))
            .attr("cy", (d:any) => yScale(d.y))
            .attr("class", 'death')
            .style("z-index", 1)
            .style("cursor", "pointer")
            .on("mouseover", mouseOver)
            .on("mousemove", mouseMove)
            .on("mouseleave", mouseOut) as any
    }

    useEffect(() => {
        if (streets && pumps && deaths) {
            streets.attr("points", (d:any) => {
                let points = "";
                d.forEach((_:any, i: number) => points += `${xScale(d[i].x)}, ${yScale(d[i].y)} `);
                return points;
            });
            deaths.attr("cx", (d:any) => xScale(d.x))
            .attr("cy", (d:any) => yScale(d.y))
            pumps.attr("cx", (d:any) => xScale(d.x))
            .attr("cy", (d:any) => yScale(d.y))
        }
    },[windowSize.width]);

    useEffect(() => {
        try {
            tooltip = d3.select("#tooltip")
            .append("div")
            .style("position", "absolute")
            .style("visibility", "hidden")
            .style("padding", "10px")
            .style("border-radius", "5px")
            .style("zIndex", 100)
            .style("background", "gray")
            .style("color", "white")
            .style("height", "70px")
            .style("width", "150px")
            .text("");

            const ds = d3Ref.current.selectAll('.death')
            ds.remove();
            deaths = d3Ref.current.selectAll('text')
                .data(props.deaths)
                .enter()
                .append("circle")
                .style("fill", "red")
                .style("r", 4)
                .attr("cx", (d:any) => xScale(d.x))
                .attr("cy", (d:any) => yScale(d.y))
                .attr("class", 'death')
                .style("z-index", 1)
                .style("cursor", "pointer")
                .on("mouseover", mouseOver)
                .on("mousemove", mouseMove)
                .on("mouseleave", mouseOut) as any
        } catch (e) {
            console.log(e);
        }
    },[props.deaths])

    return <>
        <div style={{ position: 'absolute', zIndex: 2 }}>
            <div id="tooltip"></div>
        </div>
        <div
            id="paper-plane"
            style={{
                // p: 2,
                display: 'flex',
                flexDirection: 'column',
                height: width,
                position: 'relative',
                zIndex: 1,
                overflow: "hidden",
            }}
        >
            <svg height="100%" style={{cursor: 'move'}}>
                <g height="100%">
                </g>
            </svg>
        </div>
    </>;
}

export default Map;
