import * as d3 from 'd3';
import moment from 'moment';
import React, { useEffect, useRef } from 'react';
import Deaths from './assets/deaths.json';

const DeathDistributionChart = () => {
    const margin = {top: 10, right: 20, bottom: 30, left: 30},
            height = 200 - margin.top - margin.bottom;
    let svg = useRef(d3.selectAll("div"));


    let xScale = d3.scaleBand().range([0, 300]).padding(0.4),
    yScale = d3.scaleLinear().range([height, 0]);

    const graph = () => {

        if (svg.current) {``
            svg.current.append("path")
            .datum(Deaths)
            .attr("fill", "none")
            .attr("stroke", "red")
            .attr("stroke-width", 1.5)
            //@ts-ignore
            .attr("d", d3()
                .x((d: any) => d.gender)
                .y((d: any) => d.age)
            );

            svg.current.selectAll(".bar")
            .data(Deaths)
            .enter().append("rect")
            .attr("class", "bar")
            .attr("x", (d) => xScale(d.gender) as any )
            .attr("y", (d) => yScale(d.age) )
            .attr("width", xScale.bandwidth())
            .attr("height", function(d) { return height - yScale(d.age); });
        }
    }

    const draw = () => {
        var s = d3.selectAll('#death-distribution svg g');
        var chart = d3.selectAll('#death-distribution');
        s = s.remove();
        //@ts-ignore
        var width = chart.node().getBoundingClientRect().width - 90;
        //@ts-ignore
        svg.current = d3.select("#death-distribution svg")
            .attr("width", width)
            .attr("height", height + margin.top + margin.bottom)
            .append("g")
            .attr("transform",
                "translate(" + margin.left + "," + margin.top + ")");

        xScale.domain(Deaths.map((d) => d.gender.toString() ));
        yScale.domain([0, d3.max(Deaths, (d) => d.age as any )]);

        svg.current.append("g")
        .attr("transform", "translate(0," + height + ")")
        //@ts-ignore

        y = d3.scaleLinear()
        //@ts-ignore
        .domain([0, d3.max(Deaths, (d) => +d.age)])
        .range([ height, 0 ]);

        svg.current.append("g")
        .call(d3.axisLeft(yScale));

        graph();
    }

    useEffect(() => {
        draw();

        d3.select(window)
            .on("resize", function() {
                draw();
            });
    },[]);

    return (
        <div id="death-distribution" style={{ overflow: 'visible' }}>
            <svg width="100%" style={{ overflow: 'visible' }}></svg>
        </div>
    )
}

export default DeathDistributionChart;
