import * as d3 from 'd3';
import React, { useEffect, useRef } from 'react';
import useDeathDays from './DeathDays';

const DeathChart = () => {
    const margin = {top: 10, right: 30, bottom: 30, left: 60},
            height = 200 - margin.top - margin.bottom;
    const deathDays = useDeathDays({});
    let svg = useRef(d3.selectAll("div"));


    let x: any;
    let y: any;

    const mouseOver = (_: any, l: any) => (
       console.log(l)
    );

    const graph = () => {
        let bisectDate = d3.bisector(function(d:any) { return d.date; }).left;
        let formatValue = d3.format(",");

        if (svg.current) {
            svg.current.append("path")
            .datum(deathDays)
            .attr("fill", "none")
            .attr("stroke", "red")
            .attr("stroke-width", 1.5)
            //@ts-ignore
            .attr("d", d3.line()
                .x((d: any) => x(d3.timeParse("%Y-%m-%d")(d.date)))
                .y((d: any) => y(d.deaths))
            )

        var focus = svg.current.append("g")
            .attr("class", "focus")
            .style("display", "none");

        const mousemove = (e:any) => {
            let x0 = x.invert(d3.pointer(e)[0]);
            console.log(x0)
            let i = bisectDate(deathDays, x0, 1);
            let d0 = deathDays[i - 1];
            let d1 = deathDays[i];
            //@ts-ignore
            let d = x0 - new Date(d0.date) > new Date(d1.date) - x0 ? d1 : d0;
            focus.attr("transform", "translate(" + x(d3.timeParse("%Y-%m-%d")(d.date)) + "," + y(d.deaths) + ")");
            focus.select(".tooltip-date").text(d.date);
            focus.select(".tooltip-likes").text(formatValue(d.deaths));
        }

        focus.append("circle")
            .attr("r", 5);

        focus.append("rect")
            .attr("class", "tooltip")
            .attr("width", 100)
            .attr("height", 50)
            .attr("x", 10)
            .attr("y", -22)
            .attr("rx", 4)
            .attr("ry", 4);

        focus.append("text")
            .attr("class", "tooltip-date")
            .attr("x", 18)
            .attr("y", -2);

        focus.append("text")
            .attr("x", 18)
            .attr("y", 18)
            .text("Likes:");

        focus.append("text")
            .attr("class", "tooltip-likes")
            .attr("x", 60)
            .attr("y", 18);

        svg.current.append("rect")
            .attr("class", "overlay")
            .attr("width", 100)
            .attr("height", height)
            .on("mouseover", function() { focus.style("display", null); })
            .on("mouseout", function() { focus.style("display", "none"); })
            .on("mousemove", mousemove);


        }
    }

    const draw = () => {
        var s = d3.selectAll('#death-chart svg g');
        var chart = d3.selectAll('#death-chart');
        s = s.remove();
        //@ts-ignore
        var width = chart.node().getBoundingClientRect().width - 50;
        //@ts-ignore
        svg.current = d3.select("#death-chart svg")
            .attr("width", width)
            .attr("height", height + margin.top + margin.bottom)
            .append("g")
            .attr("transform",
                "translate(" + margin.left + "," + margin.top + ")");
        const dates = deathDays.map(d => new Date(d.date));
        const domain = d3.extent(dates);
        x = d3.scaleTime()
        //@ts-ignore
        .domain(domain)
        .range([ 0, width ]);

        const xAxis = d3.axisBottom(x);

        svg.current.append("g")
        .attr("transform", "translate(0," + height + ")")
        //@ts-ignore
        .call(xAxis.ticks(d3.timeDay.every(1)).tickFormat(d3.timeFormat('%m/%d')));

        y = d3.scaleLinear()
        //@ts-ignore
        .domain([0, d3.max(deathDays, (d) => +d.deaths + 20)])
        .range([ height, 0 ]);

        svg.current.append("g")
        .call(d3.axisLeft(y));

        graph();
    }

    useEffect(() => {
        draw();

        d3.select(window)
            .on("resize", function() {
                draw();
            });
    },[]);

    useEffect(() => {
        graph();
    },[deathDays]);

    return (
        <div id="death-chart">
            <svg width="100%"></svg>
        </div>
    )
}

export default DeathChart;
