import React, { useEffect } from 'react';
import * as d3 from "d3";
const Legend = () => {

    useEffect(() => {
        var svg = d3.select("#my_dataviz")

        // Handmade legend
        svg.append("circle").attr("cx",20).attr("cy",20).attr("r", 6).style("fill", "deepskyblue")
        svg.append("circle").attr("cx",20).attr("cy",50).attr("r", 3).style("fill", "red")
        svg.append("line").attr("x1",10).attr("y1",80).attr("x2", 30).attr("y2", 80).style("stroke", "lightgrey").style("stroke-width", 6 );

        svg.append("text").attr("x", 35).attr("y", 20).text("Pumps").style("font-size", "15px").attr("alignment-baseline","middle")
        svg.append("text").attr("x", 35).attr("y", 50).text("Deaths").style("font-size", "15px").attr("alignment-baseline","middle")
        svg.append("text").attr("x", 35).attr("y", 80).text("Streets").style("font-size", "15px").attr("alignment-baseline","middle")
    },[]);

    return <>
        <div>
            <svg id="my_dataviz" height="300" width="100%"></svg>
        </div>
    </>
}

export default Legend;
