import * as d3 from 'd3';
import React, { useEffect, useRef } from 'react';
import Deaths from './assets/deaths.json';

const DeathDistributionChart = () => {
    const margin = 200,
    width = 300 - margin,
    height = 300 - margin
    let svg = useRef(d3.selectAll("div"));


    let xScale = d3.scaleBand().range([0, 300]).padding(0.4),
    yScale = d3.scaleLinear().range([height, 0]);

    // const graph = () => {

    //     if (svg.current) {
    //         svg.current.selectAll(".bar")
    //         .data(Deaths)
    //         .enter().append("rect")
    //         .attr("class", "bar")
    //         .attr("x", (d) => xScale(d.gender as any) as any )
    //         .attr("y", (d) => yScale(d.age) )
    //         .attr("width", xScale.bandwidth())
    //         .attr("height", function(d) { return height - yScale(d.age); });
    //     }
    // }

    const draw = () => {
        // var s = d3.selectAll('#death-distribution svg g');
        // var chart = d3.selectAll('#death-distribution');
        // s = s.remove();
        // //@ts-ignore
        // var width = chart.node().getBoundingClientRect().width - 90;
        // //@ts-ignore
        // svg.current = d3.select("#death-distribution svg")
        //     .attr("width", width)
        //     .attr("height", height)
        //     .append("g")
        //     .attr("transform",
        //         "translate(" + margin + "," + margin + ")");

        // xScale.domain(Deaths.map((d) => d.gender.toString() ));
        // yScale.domain([0, d3.max(Deaths, (d) => d.age as any )]);

        // svg.current.append("g")
        // .attr("transform", "translate(0," + height + ")")
        // //@ts-ignore

        // svg.current.append("g")
        // .call(d3.axisLeft(yScale));

        // graph();

        d3.selectAll('#death-distribution svg g').append("text")
        .attr("transform", "translate(100,0)")
        .attr("x", 50)
        .attr("y", 50)
        .attr("font-size", "12px")
        .text("Gender")

        var g =  d3.selectAll('#death-distribution svg g')
            //    .attr("transform", "translate(" + 100 + "," + 100 + ")");

        xScale.domain(Deaths.map(function(d) { return d.gender.toString(); }));
        yScale.domain([0, d3.max(Deaths, function(d) { return d.age as any; })]);

        g.append("g")
         .attr("transform", "translate(0," + height + ")")
         .call(d3.axisBottom(xScale))
         .append("text")
         .attr("y", height - 250)
         .attr("x", width - 100)
         .attr("text-anchor", "end")
         .text("Year");

        g.append("g")
         .call(d3.axisLeft(yScale).tickFormat(function(d){
             return d as any;
         })
         .ticks(2))
         .append("text")
         .attr("transform", "rotate(-90)")
         .attr("y", 6)
         .attr("dy", "-5.1em")
         .attr("text-anchor", "end")
         .attr("stroke", "black")
         .text("Stock Price");

        g.selectAll(".bar")
         .data(Deaths)
         .enter().append("rect")
         .attr("class", "bar")
         .attr("x", function(d) { return xScale(d.gender.toString()) as any; })
         .attr("y", function(d) { return yScale(d.age); })
         .attr("width", xScale.bandwidth())
         .attr("height", function(d) { return height - yScale(d.age); });
    }

    useEffect(() => {
        draw();

        // d3.select(window)
        //     .on("resize", function() {
        //         draw();
        //     });
    },[]);

    return (
        <div id="death-distribution" style={{ overflow: 'visible' }}>
            <svg width="100%" style={{ overflow: 'visible' }}>
                <g></g>
            </svg>
        </div>
    )
}

export default DeathDistributionChart;
