import * as d3 from 'd3';
import React, { useEffect, useRef } from 'react';
import Deaths from './assets/deaths.json';

const deathDistAge: {[key: number]: number} = {};

for (const d of Deaths) {
    if (!deathDistAge.hasOwnProperty(d.age)) {
        deathDistAge[d.age] = 1;
    } else {
        deathDistAge[d.age]++;
    }
}


const DeathDistAge = Object.entries(deathDistAge).map(([k,v]) => ({
    age: k,
    count: v
}));

const DeathsByAge = () => {
    const margin = 200,
    width = 400,
    height = 300 - margin
    let svg = useRef(d3.selectAll("div"));


    let xScale = d3.scaleBand().range([0, 300]).padding(0.4),
    yScale = d3.scaleLinear().range([height, 0]);

    const draw = () => {
        const chart = d3.selectAll('#death-distribution svg')
        //@ts-ignore
        const chartWidth = chart.node().getBoundingClientRect().width - 90;

        d3.selectAll('#death-distribution svg').append("text")
        .attr("transform", "translate(100,0)")
        .attr("x", 50)
        .attr("y", 130)
        .attr("font-size", "12px")
        .text("Age")

        var g =  d3.selectAll('#death-distribution svg')
               .attr("transform", "translate(" + 60 + "," + 30 + ")");

        xScale.domain(DeathDistAge.map((d) => d.age.toString() ));
        yScale.domain([0, d3.max(DeathDistAge, (d) => d.count as any )]);

        g.append("g")
         .attr("transform", "translate(0," + height + ")")
         .call(d3.axisBottom(xScale))
         .append("text")
         .attr("y", height - 250)
         .attr("x", chartWidth - 100)
         .attr("text-anchor", "end")
         .text("Age");

        g.append("g")
         .call(d3.axisLeft(yScale).tickFormat(function(d){
             return d as any;
         })
         .ticks(6))
         .append("text")
         .attr("transform", "rotate(-90)")
         .attr("y", 4)
         .attr("dy", "-5.1em")
         .attr("text-anchor", "end")
         .attr("stroke", "black")
        //  .text("Death Count");

        g.selectAll(".bar")
         .data(DeathDistAge)
         .enter().append("rect")
         .attr("class", "bar")
         .attr("x", function(d) { return xScale(d.age.toString()) as any; })
         .attr("y", function(d) { return yScale(d.count); })
         .attr("width", xScale.bandwidth())
         .attr("height", function(d) { return height - yScale(d.count); }).attr('fill', 'red');

         var focus = svg.current.append("g")
            .attr("class", "focus")
            .style("display", "none");

        const mousemove = (e:any) => {
            //@ts-ignore
            let x0 = xScale.invert(d3.pointer(e)[0]);
            let res = DeathDistAge.find((element) => element.age);
            if (res) {
                focus.attr("transform", "translate(" + xScale(res.age) + "," + yScale(res.count) + ")");
                focus.select(".tooltip-date").text(res.age);
                focus.select(".tooltip-likes").text(res.count);
            }

        }

        focus.append("circle")
            .attr("r", 5);

        focus.append("rect")
            .attr("class", "tooltip")
            .attr("width", 100)
            .attr("height", 50)
            .attr("x", 10)
            .attr("y", -22)
            .attr("rx", 4)
            .attr("ry", 4)
            .style("position", "absolute")
            .style("zIndex", 100);

        focus.append("text")
            .attr("class", "tooltip-date")
            .attr("x", 18)
            .attr("y", -2);

        focus.append("text")
            .attr("x", 18)
            .attr("y", 18)
            .text("Deaths:");

        focus.append("text")
            .attr("class", "tooltip-likes")
            .attr("x", 75)
            .attr("y", 18);

        var c = d3.selectAll('#death-chart');
        //@ts-ignore
        var width = c.node().getBoundingClientRect().width - 90;
        svg.current.append("rect")
            .attr("class", "overlay")
            .attr("width", width)
            .attr("height", height)
            .on("mouseover", function() { focus.style("display", null); })
            .on("mouseout", function() { focus.style("display", "none"); })
            .on("mousemove", mousemove);
    }

    useEffect(() => {
        draw();

        d3.select(window)
            .on("resize", function() {
                draw();
            });
    },[]);

    return (
        <div id="death-distribution" style={{ overflow: 'visible' }}>
            <svg width="100%" style={{ overflow: 'visible' }}>
            </svg>
        </div>
    )
}

export default DeathsByAge;
