import * as d3 from 'd3';
import React, { useEffect, useState } from 'react';

type Props = {
    deaths: any;
}

const DeathsByAge = (props: Props) => {
    const margin = 200, height = 300 - margin;  
    const [ deaths, setDeaths ] = useState<{age: string, count: number}[]>([]);

    let xScale = d3.scaleBand().range([0, 300]).padding(0.4),
    yScale = d3.scaleLinear().range([height, 0]);
    let tooltip: any = null;

    useEffect(() => {
        const deathDistAge: {[key: number]: number} = {};

        for (const d of props.deaths) {
            if (!deathDistAge.hasOwnProperty(d.age)) {
                deathDistAge[d.age] = 1;
            } else {
                deathDistAge[d.age]++;
            }
        }


        const DeathDistAge = Object.entries(deathDistAge).map(([k,v]) => ({
            age: k,
            count: v
        }));

        setDeaths(DeathDistAge)
    },[props.deaths]);

    const mouseOver = (_: any, l: any) => {
        return tooltip.style("visibility", "visible").html(`
            <b>Age</b>: ${l.age}
            <br/>
            <b>Deaths</b>: ${l.count}
        `).style("top", (_.clientY)+"px").style("left",(_.clientX)+"px")
    }

    const mouseOut = () => tooltip.style("visibility", "hidden");

    const draw = () => {
        const chart = d3.selectAll('#death-distribution svg')
        chart.remove();
        //@ts-ignore
        const chartWidth = chart.node().getBoundingClientRect().width - 90;

        var g =  d3.selectAll('#death-distribution svg')
               .attr("transform", "translate(" + 60 + "," + 30 + ")");

        xScale.domain(deaths.map((d) => d.age.toString() ));
        yScale.domain([0, d3.max(deaths, (d) => d.count as any )] as any);

        g.append("g")
         .attr("transform", "translate(0," + height + ")")
         .call(d3.axisBottom(xScale))
         .append("text")
         .attr("y", height - 250)
         .attr("x", chartWidth - 100)
         .attr("text-anchor", "end")
         .text("Age");

        g.append("g")
         .call(d3.axisLeft(yScale).tickFormat(function(d){
             return d as any;
         })
         .ticks(6))
         .append("text")
         .attr("transform", "rotate(-90)")
         .attr("y", 4)
         .attr("dy", "-5.1em")
         .attr("text-anchor", "end")
         .attr("stroke", "black")


        g.selectAll(".bar")
            .data(deaths)
            .enter().append("rect")
            .attr("class", "bar")
            .attr("x", (d) => xScale(d.age.toString()) as any)
            .attr("y", (d) => yScale(d.count))
            .attr("width", xScale.bandwidth())
            .attr("height", (d) => height - yScale(d.count)).attr('fill', 'red')
            .on("mouseenter", mouseOver)
            .on("mouseleave", mouseOut) as any

        tooltip = d3.select("#tooltip-here")
            .append("div")
            .style("position", "absolute")
            .style("visibility", "hidden")
            .style("padding", "10px")
            .style("border-radius", "5px")
            .style("zIndex", 100)
            .style("background", "gray")
            .style("color", "white")
            .style("height", "70px")
            .style("width", "150px")
            .text("");
    }

    useEffect(() => {
        draw();

        d3.select(window)
            .on("resize", function() {
                draw();
            });
    },[props.deaths]);

    return (
        <>
            <div id="tooltip-here" style={{zIndex: 1}}></div>
            <div id="death-distribution" style={{ overflow: 'visible' }}>
                <svg width="100%" style={{ overflow: 'visible' }}>
                </svg>
            </div>
        </>
    )
}

export default DeathsByAge;
