import React from 'react';
import { FormControl, InputLabel, MenuItem, Select, Slider } from '@mui/material';
import { useRecoilState } from 'recoil';
import Sex from './State/Sex';
import Age from './State/Age';

const Filter = () => {
    const [ sex, setSex ] = useRecoilState(Sex);
    const [ age, setAge ] = useRecoilState(Age);

    return (
        <>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Age</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={sex}
                    label="Gender"
                    onChange={(e) => {
                        setSex(e.target.value as any);
                    }}
                >
                    <MenuItem value={-1}>All</MenuItem>
                    <MenuItem value={0}>Female</MenuItem>
                    <MenuItem value={1}>Male</MenuItem>
                </Select>
            </FormControl>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Age Range</InputLabel>
                <Slider
                    getAriaLabel={() => 'Age Range'}
                    value={[age.min, age.max]}
                    onChange={(e) => console.log(e)}
                    valueLabelDisplay="auto"
                />
            </FormControl>
        </>
    );
}

export default Filter;
