import * as d3 from 'd3';
import React, { useEffect, useRef, useState } from 'react';
import useDeathDays from './DeathDays';
import useWindowSize from './useWindowSize';

const DeathChart = () => {
    const margin = {top: 10, right: 30, bottom: 30, left: 60},
            height = 200 - margin.top - margin.bottom;
    const deathDays = useDeathDays({});
    let svg = useRef(d3.selectAll("div"));
    const windowSize = useWindowSize();
    const [ width, setWidth ] = useState(1000 - margin.left - margin.right,);


    let x: any;
    let y: any;

    useEffect(() => {
        //@ts-ignore
        svg.current = d3.select("#death-chart svg")
            .attr("width", width + margin.left + margin.right)
            .attr("height", height + margin.top + margin.bottom)
        .append("g")
            .attr("transform",
                "translate(" + margin.left + "," + margin.top + ")");
        const dates = deathDays.map(d => new Date(d.date));
        const domain = d3.extent(dates);
        x = d3.scaleTime()
            //@ts-ignore
            .domain(domain)
            .range([ 0, width ]);

        const xAxis = d3.axisBottom(x);

        svg.current.append("g")
            .attr("transform", "translate(0," + height + ")")
            //@ts-ignore
            .call(xAxis.ticks(d3.timeDay.every(1)).tickFormat(d3.timeFormat('%m/%d')));
            // .call(x.ticks(41));
            // .call(d3.axisBottom(x));

        y = d3.scaleLinear()
            //@ts-ignore
            .domain([0, d3.max(deathDays, (d) => +d.deaths + 20)])
            .range([ height, 0 ]);

        svg.current.append("g")
            .call(d3.axisLeft(y));

        d3.select(window)
            .on("resize", function() {
                var targetWidth = window.innerWidth;
                svg.current.attr("width", targetWidth);
            });
    },[]);

    useEffect(() => {
        // setWidth(windowSize.width - margin.left - margin.right);
        console.log(windowSize.width);
        d3.select("#death-chart svg")
            .attr("width", windowSize.width + margin.left + margin.right)
    },[windowSize.width])

    useEffect(() => {
        if (svg.current) {
            svg.current.append("path")
            .datum(deathDays)
            .attr("fill", "none")
            .attr("stroke", "red")
            .attr("stroke-width", 1.5)
            //@ts-ignore
            .attr("d", d3.line()
                .x((d: any) => x(d3.timeParse("%Y-%m-%d")(d.date)))
                .y((d: any) => y(d.deaths))
            )
        }
    },[deathDays]);

    return (
        <div id="death-chart">
            <svg></svg>
        </div>
    )
}

export default DeathChart;
