import * as d3 from 'd3';
import React, { useEffect, useRef } from 'react';
import useDeathDays from './DeathDays';

const DeathChart = () => {
    const deathDays = useDeathDays({});
    let svg = useRef(d3.select("#death-chart"));

    const margin = {top: 10, right: 30, bottom: 30, left: 60},
            width = 460 - margin.left - margin.right,
            height = 400 - margin.top - margin.bottom;
    let x: any;

    // Add Y axis
    let y: any;

    useEffect(() => {
        // append the svg object to the body of the page
        //@ts-ignore
        svg.current = d3.select("#death-chart")
        .append("svg")
            .attr("width", width + margin.left + margin.right)
            .attr("height", height + margin.top + margin.bottom)
        .append("g")
            .attr("transform",
                "translate(" + margin.left + "," + margin.top + ")");

        x = d3.scaleTime()
        //@ts-ignore
        .domain(d3.extent(deathDays, (d) => d3.timeParse("%m-%d")(d.date)))
        .range([ 0, width ]);
        svg.current.append("g")
        .attr("transform", "translate(0," + height + ")")
        .call(d3.axisBottom(x));

        // Add Y axis
        y = d3.scaleLinear()
            //@ts-ignore
            .domain([0, d3.max(deathDays, (d) => +d.deaths)])
            .range([ height, 0 ]);
            svg.current.append("g")
            .call(d3.axisLeft(y));
    },[]);

    useEffect(() => {
        if (svg.current) {
            svg.current.append("path")
            .datum(deathDays)
            .attr("fill", "none")
            .attr("stroke", "red")
            .attr("stroke-width", 1.5)
            //@ts-ignore
            .attr("d", d3.line()
                .x((d: any) => x(d3.timeParse("%m-%d")(d.date)))
                .y((d: any) => { console.log(d); return y(d.deaths)})
            )
        }
    },[deathDays]);

    return (
        <div id="death-chart">
        </div>
    )
}

export default DeathChart;
