import * as d3 from 'd3';
import React, { useEffect, useRef, useState } from 'react';

type Props = {
    deaths: any;
}

const DeathsByGender = (props: Props) => {
    const height = 120
    let svg = useRef(d3.selectAll("div"));
    const [ deaths, setDeaths ] = useState<{gender: string, count: number}[]>([]);


    let xScale = d3.scaleBand().range([0, 300]).padding(0.4),
    yScale = d3.scaleLinear().range([height, 0]);

    let tooltip: any = null;

    useEffect(() => {
        const deathDistGender: {[key: number]: number} = {0: 0, 1: 1};

        for (const d of props.deaths) {
            deathDistGender[d.gender]++;
        }

        const DeathDistGender = [
            {
                gender: 'Male',
                count: deathDistGender[1]
            },
            {
                gender: 'Female',
                count: deathDistGender[0]
            }
        ];

        setDeaths(DeathDistGender)
    },[props.deaths]);

    useEffect(() => {
        tooltip = d3.select("#tooltip-there")
            .append("div")
            .style("position", "absolute")
            .style("visibility", "hidden")
            .style("padding", "10px")
            .style("border-radius", "5px")
            .style("zIndex", 100)
            .style("background", "gray")
            .style("color", "white")
            .style("height", "70px")
            .style("width", "150px")
            .text("");
    }, []);

    const mouseOver = (_: any, l: any) => {
        return tooltip.style("visibility", "visible").html(`
            <b>Gender</b>: ${l.gender}
            <br/>
            <b>Deaths</b>: ${l.count}
        `).style("top", (_.clientY)+"px").style("left",(_.clientX)+"px")
    }

    const mouseOut = () => tooltip.style("visibility", "hidden");

    const draw = () => {
        const chart = d3.selectAll('#death-gender svg')
        chart.selectAll('g').remove();
        chart.selectAll('.bar').remove();
        //@ts-ignore
        const chartWidth = chart.node().getBoundingClientRect().width - 90;

        var g =  d3.selectAll('#death-gender svg')
               .attr("transform", "translate(" + 60 + "," + 30 + ")");

        xScale.domain(deaths.map((d) => d.gender.toString() ));
        yScale.domain([0, d3.max(deaths, (d) => d.count as any )]);

        g.append("g")
         .attr("transform", "translate(0," + height + ")")
         .call(d3.axisBottom(xScale))
         .append("text")
         .attr("y", height)
         .attr("x", chartWidth - 100)
         .attr("text-anchor", "end")

        g.append("g")
         .call(d3.axisLeft(yScale).tickFormat(function(d){
             return d as any;
         })
         .ticks(5))
         .append("text")
         .attr("transform", "rotate(-90)")
         .attr("y", 4)
         .attr("dy", "-5.1em")
         .attr("text-anchor", "end")
         .attr("stroke", "black")
        //  .text("Death Count");

        g.selectAll(".bar")
            .data(deaths)
            .enter().append("rect")
            .attr("class", "bar")
            .attr("x", function(d) { return xScale(d.gender.toString()) as any; })
            .attr("y", function(d) { return yScale(d.count); })
            .attr("width", xScale.bandwidth())
            .attr("height", function(d) { return height - yScale(d.count); }).attr('fill', 'red')
            .on("mouseenter", mouseOver)
            .on("mouseleave", mouseOut) as any;
    }

    useEffect(() => {
        draw();

        d3.select(window)
            .on("resize", function() {
                draw();
            });
    },[props.deaths]);

    return (
        <>
            <div id="tooltip-there" style={{zIndex: 1}}></div>
            <div id="death-gender" style={{ overflow: 'visible' }}>
                <svg width="100%" style={{ overflow: 'visible' }}>
                </svg>
            </div>
        </>

    )
}

export default DeathsByGender;
